import { status } from '../utils';

export function getGroup(id) {
    return fetch(`http://127.0.0.1:8000/api/groups/${id}`)
    .then(status).catch( e=> { console.log(e) })
}

export function getGroups() {
    return fetch(`http://127.0.0.1:8000/api/groups/`)
    .then(status).catch( e=> { console.log(e) })
}

export function joinGroup(userData, userID, token){
    return fetch(`http://127.0.0.1:8000/api/members/${userID}/change_password/`, {
        method: 'PUT', 
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Token ${token}`
        },
        body: JSON.stringify(userData)
    }).then(status).catch( e=> { console.log(e) })
}
