import React, {useState, useEffect} from 'react';
import './App.css';


function App() {

const [groups, setGroup] = useState(null);

  useEffect(() => {
    const getData = async () => {
      await fetch('http://127.0.0.1:8000/api/groups/')
      .then( resp => resp.json())
      .then( data=> {
        setGroup(data);
      })
    }
    getData();
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        { groups && groups.map(group => {
          return <p>{group.name}: {group.location}</p>
        })}

      </header>
    </div>
  )
}

export default App;
