import { amber, lightBlue, purple } from '@mui/material/colors';
import { createTheme } from '@mui/material/styles';

const theme = createTheme ({
    palette: {
        primary:amber,
        secondary:lightBlue
    },

    colors: {
        bgColor: '#3e3e3d',
        bgLigmain
    }
});

export default theme;
