import { amber, lightBlue, purple } from '@mui/material/colors';
import { createTheme } from '@mui/material/styles';

const theme = createTheme ({
    palette: {
        primary:amber,
        secondary:lightBlue
    },

    colors: {
        bgColor: '#3e3e3d',
        bgLightColor: '#888',
        mainAccentColor: '#fecc01',
    },

    container: {
        width: '200px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        position: 'relative'
    }
});

export default theme;
